public class MainActivity extends AppCompatActivity {
    private ProgressBar progressBar; // Modern replacement for ProgressDialog
    private Handler handler = new Handler(); // Used to update progress on the main thread
    private int progress = 0;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_main);
        Button downloadButton = findViewById(R.id.downloadButton);
        progressBar = findViewById(R.id.progressBar);

        downloadButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                progressBar.setVisibility(View.VISIBLE); // Show the progress bar
                simulateDownload(); // Start download simulation
            }
        });
    }
        void simulateDownload() {
            progress = 0; // Reset progress

            new Thread(new Runnable() {
                @Override
                public void run() {
                    while (progress <= 100) {
                        try {
                            Thread.sleep(50); // Simulate download delay (50ms per 1% increment)
                        } catch (InterruptedException e) {
                            e.printStackTrace();
                        }
                        progress += 1;

                        handler.post(new Runnable() {
                            @Override
                            public void run() {
                                progressBar.setProgress(progress); // Update the progress bar
                                if (progress >= 100) {
                                    progressBar.setVisibility(View.GONE); // Hide the progress bar when done
                                }
                            }
                        });

                    }
                }
            }).start();
        }
    }', 'Lab pgm 8 long short toast

